import React, { useState } from "react";
import jquery from 'jquery';
import './SignIn.scss';

const lrconfig = {
  apiKey: 'cd2b68c2-edeb-4fb1-a01f-e2ca5cbd31cf', //LoginRadius API key
};

const [hiddenButton, setButton] = useState('none');
let loginradius = {};
//let hiddenButton = 'none';

jquery(window).on('load', function() {
  if (window.LoginRadiusV2) {
    loginradius = new window.LoginRadiusV2(lrconfig);
    loginradius.api.init(lrconfig);
    hiddenButton = 'block';
    console.log(hiddenButton);
  }
});

const LoginButton = () => {
  const loginButtonHandler = () => {
    loginradius.api.login({
      emailid: emailValue,
      password: passwordValue
    },
    (successResponse) => {
      //Here you will get the access Token of 
      console.log(successResponse);
    },
    (errors) => {
      console.log(errors);
    });
  }

  const [emailValue, updateEmailValue] = useState("");
  const [passwordValue, updatePasswordValue] = useState("");

  return (
    <>
      <div className="form-container-signin">
        <div id="signin">
          <div className="signin-input">
            <div className="signin-labels">
              <p>Email:</p>
              <p>Passowrd:</p>
            </div>
            <div className="signin-fields">
              <input type="text"
                value={emailValue}
                onChange={(e) => { updateEmailValue(e.target.value) }}
                placeholder={"email"}
                name="email"
                type="text"
                maxLength="20"/>
              <input type="password"
                value={passwordValue}
                onChange={(e) => { updatePasswordValue(e.target.value) }}
                placeholder={"Password"}
                name="passowrd"
                type="text"
                maxLength="20"/>
            </div>
          </div>
          <br />
          <div className="sigin-submit" style={{display: hiddenButton}}>
            <button onClick={() => loginButtonHandler()}>Log In</button>
          </div>
        </div>
      </div>
    </>
  );
};

export default LoginButton;
